import Cell from '../lib'

for (let i = 0; i < 3; i++) {
  for (let j = 0; j < 3; j++) {
    let align = 'left'
    if (j === 0) {
      align = 'right'
    }
    const config = {
      length: 40,
      backgroundColor: 244,
      foregroundColor: 'white',
      content: 'hello',
      align,
    }
    const cell = new Cell(config)
    cell.write()
  }
  process.stdout.write('\n')
}
