import { expect } from 'chai'
import Cell from '../lib'

function delay(ms) {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(true)
    }, ms || 1000)
  })
}

describe('Cell', () => {
  it('left', async () => {
    const config = {
      length: 10,
      align: 'left',
      backgroundColor: 'magenta',
      content: 'hello',
    }

    const cell = new Cell(config)
    cell.write()
  })

  it('right', async () => {
    const config = {
      length: 10,
      align: 'right',
      backgroundColor: 'magenta',
      content: 'hello',
    }

    const cell = new Cell(config)
    cell.write()
  })

  it('center', async () => {
    const config = {
      length: 10,
      align: 'center',
      backgroundColor: 'magenta',
      content: 'hello',
    }

    const cell = new Cell(config)
    cell.write()
  })

  it('foreground red', async () => {
    const config = {
      length: 10,
      align: 'center',
      backgroundColor: 'magenta',
      foregroundColor: 'red',
      content: 'hello',
    }

    const cell = new Cell(config)
    cell.write()
  })
})
